import React, { useState } from 'react';
import './App.css';
import { AgGridColumn, AgGridReact } from 'ag-grid-react';
import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-alpine.css';

const App = () => {
  var mlb_item_order = ['Overall','AL','NL','C','1B','2B','3B','SS','LF','CF','RF','DH'];
  var aaa_item_order = ['Overall','INT','PCL','C','1B','2B','3B','SS','LF','CF','RF','DH'];
  var lgArray = {'MLB' : mlb_item_order, 'AAA': aaa_item_order}

  let leagueAverages = require('../src/LeagueAvg.json')

  const [gridApi, setGridApi] = useState(null);
  const [gridColumnApi, setGridColumnApi] = useState(null);

  var html = '';

  for (var key in lgArray) {
    html = html + (
      <div className="ag-theme-alpine" style={{ height: 600, width: 2000 }}>
          <AgGridReact
              rowData={mapOrder(leagueAverages.filter(leagueAverages => leagueAverages.league == key), lgArray[key], 'split')}>
              <AgGridColumn headerName="MLB" headerClass="css-header">
                <AgGridColumn field="split" headerName=""></AgGridColumn>
                <AgGridColumn field="age" headerName="Age" valueFormatter={oneDecimal}></AgGridColumn>
                <AgGridColumn field="prospect_age" headerName="Prospect Age" valueFormatter={oneDecimal}></AgGridColumn>
                <AgGridColumn field="avg" headerName="AVG" valueFormatter={threeDecimals}></AgGridColumn>
                <AgGridColumn field="obp" headerName="OBP" valueFormatter={threeDecimals}></AgGridColumn>
                <AgGridColumn field="slg" headerName="SLG" valueFormatter={threeDecimals}></AgGridColumn>
                <AgGridColumn field="ops" headerName="OPS" valueFormatter={threeDecimals}></AgGridColumn>
                <AgGridColumn field="tm_ops" headerName="TM OPS" valueFormatter={threeDecimals}></AgGridColumn>
                <AgGridColumn field="woba" headerName="wOBA" valueFormatter={threeDecimals}></AgGridColumn>
                <AgGridColumn field="babip" headerName="BABIP" valueFormatter={threeDecimals}></AgGridColumn>
                <AgGridColumn field="avg_exit_velo" headerName="Exit Velo" valueFormatter={oneDecimal}></AgGridColumn>
                <AgGridColumn field="exit_velo_90_plus" headerName="% > 90 mph" valueFormatter={rateStat}></AgGridColumn>
                <AgGridColumn field="exit_velo_100_plus" headerName="% > 100 mph" valueFormatter={rateStat}></AgGridColumn>
                <AgGridColumn field="swing_per" headerName="Sw" valueFormatter={rateStat}></AgGridColumn>
                <AgGridColumn field="swing_per_kzone" headerName="Sw In Zone" valueFormatter={rateStat}></AgGridColumn>
                <AgGridColumn field="chase_per" headerName="Ch" valueFormatter={rateStat}></AgGridColumn>
                <AgGridColumn field="chase_per_2k" headerName="Ch 2K" valueFormatter={rateStat}></AgGridColumn>
                <AgGridColumn field="miss_per" headerName="Ms" valueFormatter={rateStat}></AgGridColumn>
                <AgGridColumn field="miss_chase_per" headerName="Ms Ch" valueFormatter={rateStat}></AgGridColumn>
                <AgGridColumn field="gb_per_fb" headerName="G/F" valueFormatter={oneDecimal}></AgGridColumn>
                <AgGridColumn field="ab_per_hr" headerName="AB/HR" valueFormatter={oneDecimal}></AgGridColumn>
                <AgGridColumn field="ab_per_ubb" headerName="AB/UBB" valueFormatter={oneDecimal}></AgGridColumn>
                <AgGridColumn field="ab_per_so" headerName="AB/SO" valueFormatter={oneDecimal}></AgGridColumn>
                <AgGridColumn field="gb_per" headerName="GB%" valueFormatter={rateStat}></AgGridColumn>
                <AgGridColumn field="ubb_per" headerName="UBB%" valueFormatter={rateStat}></AgGridColumn>
                <AgGridColumn field="so_per" headerName="K%" valueFormatter={rateStat}></AgGridColumn>
              </AgGridColumn>
          </AgGridReact>
      </div>
    ).toString();
  }

  return (html);

  function oneDecimal(params) {
    return params.value.toFixed(1);
  }

  function threeDecimals(params) {
    return params.value.toFixed(3).replace(/^0+/, '');
  }

  function rateStat(params) {
    return toInt(params.value).toString() + '%';
  }

  function toInt(number) {
    return Math.round(100 * number);
  }

