          .map((leagueAverages) => (
            <p>{leagueAverages.league} / {leagueAverages.split}</p>
        ))}
        </p>
        <p>
        {mapOrder(leagueAverages.filter(leagueAverages => leagueAverages.league == 'AAA'), aaa_item_order, 'split')
          .map((leagueAverages) => (
            <p>{leagueAverages.league} / {leagueAverages.split}</p>
        ))}
        </p>        
      </div>
    ) */
  }


function mapOrder (array, order, key) {
  array.sort( function (a, b) {
    var A = a[key], B = b[key];

    if (order.indexOf(A) > order.indexOf(B)) {
      return 1;
    } else {
      return -1;
    }

  });

  return array;
};

export default App;
