import React, { Component } from 'react';
import { render } from 'react-dom';
import './App.css';
import { AgGridColumn, AgGridReact } from 'ag-grid-react';
import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-alpine.css';

const App = () => {
  var mlb_item_order = ['Overall','AL','NL','C','1B','2B','3B','SS','LF','CF','RF','DH'];
  var aaa_item_order = ['Overall','INT','PCL','C','1B','2B','3B','SS','LF','CF','RF','DH'];
  var lgArray = {'MLB' : mlb_item_order, 'AAA': aaa_item_order}
  var rowsToPin = ['Overall','AL','NL','INT','PCL']

  let leagueAverages = require('../src/LeagueAvg.json')
  let content = [];

  this.state = {
    columnDefs: [
      {
        headerName: key,
        children: [
          {
            headerName: '',
            valueGetter: splitTitle
          },
          {
            headerName: 'Age',
            field: 'age',
            valueFormatter: oneDecimal
          },
          {
            headerName: 'Prospect Age',
            field: 'prospect_age',
            valueFormatter: oneDecimal
          },
          {
            headerName: 'AVG',
            field: 'avg',
            valueFormatter: threeDecimals
          },
          {
            headerName: 'OBP',
            field: 'obp',
            valueFormatter: threeDecimals
          },
          {
            headerName: 'SLG',
            field: 'slg',
            valueFormatter: threeDecimals
          },
          {
            headerName: 'OPS',
            field: 'ops',
            valueFormatter: threeDecimals
          },
          {
            headerName: 'TM OPS',
            field: 'tm_ops',
            valueFormatter: threeDecimals
          },
          {
            headerName: 'wOBA',
            field: 'woba',
            valueFormatter: threeDecimals
          },
          {
            headerName: 'BABIP',
            field: 'babip',
            valueFormatter: threeDecimals
          },
          {
            headerName: 'Exit Velo',
            field: 'avg_exit_velo',
            valueFormatter: oneDecimal
          },
          {
            headerName: '% > 90 mph',
            field: 'exit_velo_90_plus',
            valueFormatter: rateStat
          },
          {
            headerName: '% > 100 mph',
            field: 'exit_velo_100_plus',
            valueFormatter: rateStat
          },
          {
            headerName: 'Sw',
            field: 'swing_per',
            valueFormatter: rateStat
          },
          {
            headerName: 'Sw In Zone',
            field: 'swing_per_kzone',
            valueFormatter: rateStat
          },
          {
            headerName: 'Ch',
            field: 'chase_per',
            valueFormatter: rateStat
          },
          {
            headerName: 'Ch 2K',
            field: 'chase_per_2k',
            valueFormatter: rateStat
          },
          {
            headerName: 'Ms',
            field: 'miss_per',
            valueFormatter: rateStat
          },
          {
            headerName: 'Ms Ch',
            field: 'miss_chase_per',
            valueFormatter: rateStat
          },
          {
            headerName: 'G/F',
            field: 'gb_per_fb',
            valueFormatter: oneDecimal
          },
          {
            headerName: 'AB/HR',
            field: 'ab_per_hr',
            valueFormatter: oneDecimal
          },
          {
            headerName: 'AB/UBB',
            field: 'ab_per_ubb',
            valueFormatter: oneDecimal
          },
          {
            headerName: 'AB/SO',
            field: 'ab_per_so',
            valueFormatter: oneDecimal
          },
          {
            headerName: 'GB%',
            field: 'gb_per',
            valueFormatter: rateStat
          },
          {
            headerName: 'UBB%',
            field: 'ubb_per',
            valueFormatter: rateStat
          },
          {
            headerName: 'K%',
            field: 'so_per',
            valueFormatter: rateStat
          },
        ]
      }
    ],
    defaultColDef: {
      flex: 1,
      sortable: true,
      wrapText: true,
      autoHeight: true,
      headerComponentParams: {
        template:
          '<div class="ag-cell-label-container" role="presentation">' +
          '  <span ref="eMenu" class="ag-header-icon ag-header-cell-menu-button"></span>' +
          '  <div ref="eLabel" class="ag-header-cell-label" role="presentation">' +
          '    <span ref="eSortOrder" class="ag-header-icon ag-sort-order"></span>' +
          '    <span ref="eSortAsc" class="ag-header-icon ag-sort-ascending-icon"></span>' +
          '    <span ref="eSortDesc" class="ag-header-icon ag-sort-descending-icon"></span>' +
          '    <span ref="eSortNone" class="ag-header-icon ag-sort-none-icon"></span>' +
          '    <span ref="eText" class="ag-header-cell-text" role="columnheader" style="white-space: normal;"></span>' +
          '    <span ref="eFilter" class="ag-header-icon ag-filter-icon"></span>' +
          '  </div>' +
          '</div>',
      },
    },
    rowData: [
      {
        a: 1,
        b:
          'Vivamus viverra interdum sapien, non finibus risus venenatis vitae',
        c: 'Praesent elementum consequat rhoncus',
      },
      {
        a: 2,
        b: 'Donec eget sagittis ipsum',
        c: 'Aliquam at efficitur libero, eget viverra turpis',
      },
      {
        a: 3,
        b: 'Etiam vehicula consectetur vestibulum',
        c:
          'Morbi accumsan ultricies orci, et efficitur magna malesuada sit amet',
      },
    ],
  };

  onGridReady = params => {
    this.gridApi = params.api;
    this.gridColumnApi = params.columnApi;
  };

  for (var key in lgArray) {
    content.push(
      <p><div className="ag-theme-alpine" style={{ height: 625, width: 1800 }}>
      <AgGridReact 
        rowData = { 
          mapOrder(leagueAverages
            .filter(leagueAverages => leagueAverages.league == key)
            .filter(leagueAverages => !rowsToPin.includes(leagueAverages.split)), lgArray[key], 'split')} 
        pinnedTopRowData = { 
          mapOrder(leagueAverages
            .filter(leagueAverages => leagueAverages.league == key)
            .filter(leagueAverages => rowsToPin.includes(leagueAverages.split)), lgArray[key], 'split')}
      >
        <AgGridColumn headerName={key}>
          <AgGridColumn valueGetter={splitTitle} headerName=""></AgGridColumn>
          <AgGridColumn field="age" headerName="Age" valueFormatter={oneDecimal} sortable='true'></AgGridColumn>
          <AgGridColumn field="prospect_age" headerName="Prospect Age" valueFormatter={oneDecimal} sortable='true'></AgGridColumn>
          <AgGridColumn field="avg" headerName="AVG" valueFormatter={threeDecimals} sortable='true'></AgGridColumn>
          <AgGridColumn field="obp" headerName="OBP" valueFormatter={threeDecimals} sortable='true'></AgGridColumn>
          <AgGridColumn field="slg" headerName="SLG" valueFormatter={threeDecimals} sortable='true'></AgGridColumn>
          <AgGridColumn field="ops" headerName="OPS" valueFormatter={threeDecimals} sortable='true'></AgGridColumn>
          <AgGridColumn field="tm_ops" headerName="TM OPS" valueFormatter={threeDecimals} sortable='true'></AgGridColumn>
          <AgGridColumn field="woba" headerName="wOBA" valueFormatter={threeDecimals} sortable='true'></AgGridColumn>
          <AgGridColumn field="babip" headerName="BABIP" valueFormatter={threeDecimals} sortable='true'></AgGridColumn>
          <AgGridColumn field="avg_exit_velo" headerName="Exit Velo" valueFormatter={oneDecimal} sortable='true'></AgGridColumn>
          <AgGridColumn field="exit_velo_90_plus" headerName="% > 90 mph" valueFormatter={rateStat} sortable='true'></AgGridColumn>
          <AgGridColumn field="exit_velo_100_plus" headerName="% > 100 mph" valueFormatter={rateStat} sortable='true'></AgGridColumn>
          <AgGridColumn field="swing_per" headerName="Sw" valueFormatter={rateStat} sortable='true'></AgGridColumn>
          <AgGridColumn field="swing_per_kzone" headerName="Sw In Zone" valueFormatter={rateStat} sortable='true'></AgGridColumn>
          <AgGridColumn field="chase_per" headerName="Ch" valueFormatter={rateStat} sortable='true'></AgGridColumn>
          <AgGridColumn field="chase_per_2k" headerName="Ch 2K" valueFormatter={rateStat} sortable='true'></AgGridColumn>
          <AgGridColumn field="miss_per" headerName="Ms" valueFormatter={rateStat} sortable='true'></AgGridColumn>
          <AgGridColumn field="miss_chase_per" headerName="Ms Ch" valueFormatter={rateStat} sortable='true'></AgGridColumn>
          <AgGridColumn field="gb_per_fb" headerName="G/F" valueFormatter={oneDecimal} sortable='true'></AgGridColumn>
          <AgGridColumn field="ab_per_hr" headerName="AB/HR" valueFormatter={oneDecimal} sortable='true'></AgGridColumn>
          <AgGridColumn field="ab_per_ubb" headerName="AB/UBB" valueFormatter={oneDecimal} sortable='true'></AgGridColumn>
          <AgGridColumn field="ab_per_so" headerName="AB/SO" valueFormatter={oneDecimal} sortable='true'></AgGridColumn>
          <AgGridColumn field="gb_per" headerName="GB%" valueFormatter={rateStat} sortable='true'></AgGridColumn>
          <AgGridColumn field="ubb_per" headerName="UBB%" valueFormatter={rateStat} sortable='true'></AgGridColumn>
          <AgGridColumn field="so_per" headerName="K%" valueFormatter={rateStat} sortable='true'></AgGridColumn>
        </AgGridColumn>
      </AgGridReact>
      </div></p>
    );
  }

  return (
    <div>
      {content}
    </div>
  );

  function splitTitle(params) {
    var split = params.data.split;
    if (split == 'Overall') {
      return `${params.data.league} ${split}`;
    }
    else {
      return split;
    }
  }

  function oneDecimal(params) {
    return params.value.toFixed(1);
  }

  function threeDecimals(params) {
    return params.value.toFixed(3).replace(/^0+/, '');
  }

  function rateStat(params) {
    return toInt(params.value).toString() + '%';
  }

  function toInt(number) {
    return Math.round(100 * number);
  }
}

function mapOrder (array, order, key) {
  array.sort( function (a, b) {
    var A = a[key], B = b[key];

    if (order.indexOf(A) > order.indexOf(B)) {
      return 1;
    } else {
      return -1;
    }

  });

  return array;
};

export default App;
